response=[]

def adjust_month(dates):
    month='0'+str(int(dates[1])-1)
    if len(month)>2:
        return month[1:]
    else:
        return month
    
def spline_chart_transform(date,score):
    dates=date.split('-')
    #month='0'+str(int(dates[1])-1)
    month = adjust_month(dates)
    
    #print(month)
    #[Date.UTC(1971, 0, 26), 0.72]
    json_obj='[Date.UTC({0}, {1}, {2}), {3}]'.format(dates[0],month,dates[2],score)
    #print(json_obj)
    response.append(json_obj)

